import '../App.css';
import {initializeApp} from 'firebase/app';
import {getAuth, GoogleAuthProvider, signInWithPopup} from 'firebase/auth';
import {useAuthState} from 'react-firebase-hooks/auth';
import {useEffect, useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';



export default function LoginBtn({googleSignIn, emailSignIn ,type, showLogin, email, password}){
    if(type === "Email"){
        return (
            <button className="btn btn-secondary" onClick={() => {emailSignIn(); showLogin()}}>Sign In <i class="fa-solid fa-envelope"></i></button>
        )
    } else if(type === "Google") {
        return (
            <button className="btn btn-danger" onClick={() => {googleSignIn(); showLogin()}}>Sign in with Google <i class="fa-brands fa-google"></i></button>
        )
    }
}
