import './App.css';
import {initializeApp} from 'firebase/app';
import {getAuth, GoogleAuthProvider, signInWithPopup} from 'firebase/auth';
import {useAuthState} from 'react-firebase-hooks/auth';
import Home from './Components/Home';
import LoginPage from './Components/LoginBox';

const firebaseConfig = {
  apiKey: "AIzaSyD99Ps3ewiDL7WTtaCJaDqBmo5dmSRjuCo",
  authDomain: "cook-mastershare.firebaseapp.com",
  projectId: "cook-mastershare",
  storageBucket: "cook-mastershare.appspot.com",
  messagingSenderId: "901848339378",
  appId: "1:901848339378:web:ea76b02d8ef1cff6670aab",
  measurementId: "G-11NKHJEW95"
};

let app = initializeApp(firebaseConfig);
let auth = getAuth(app);

function App() {
  let showLogin = Boolean(false);
  const [user] = useAuthState(auth);
  async function signInWithGoogle(){
    let provider = new GoogleAuthProvider();
    await signInWithPopup(auth, provider);
  }
  function signOut(){
    auth.signOut();
  }
  if(showLogin){
    return (
      <LoginPage/>
    )
  }
  else {
    return (
      <>
        {user ? <Home type={user.displayName} btnEvent={showLogin=true}/> : <Home type="Login" btnEvent={showLogin = true}/>} 
      </>
    )
  }
}

export default App;
