import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../css/new-recipe.css'
import '../App.css';
import { getFirestore } from "firebase/firestore";
import { doc, setDoc } from "firebase/firestore"; 
import { collection, query, where, getDocs, addDoc } from "firebase/firestore";
import {storage, ref, uploadBytes} from 'firebase/storage';
import { useState } from "react";


const firebaseConfig = {
  apiKey: "AIzaSyD99Ps3ewiDL7WTtaCJaDqBmo5dmSRjuCo",
  authDomain: "cook-mastershare.firebaseapp.com",
  projectId: "cook-mastershare",
  storageBucket: "cook-mastershare.appspot.com",
  messagingSenderId: "901848339378",
  appId: "1:901848339378:web:ea76b02d8ef1cff6670aab",
  measurementId: "G-11NKHJEW95"
};

let app = initializeApp(firebaseConfig);
let auth = getAuth(app);
let db = getFirestore(app);
let recipes = await getDocs(collection(db, "recipes"));

export default function NewRecipe({addToDB}){
    const [imageUpload, setImageUpload] = useState('');
    function selectFile(event){
        document.getElementById('getFile').click();
        event.preventDefault();
    }
    function addRec(event){
        const name = document.getElementById('recipename').value;
        const descr = document.getElementById('descr').value;
        const cont = document.getElementById('cont').value;
        addToDB(name, descr, cont);
        event.preventDefault();
    }
    return (
        <section className="createrecipe" id="createrecipe">
            <h1 className="text-white">Create new recipe</h1>
            <div className="hr"></div>
            <form className="form upload">
            <div className="photo">
                <h3>Upload your photo</h3>
                <button
                className="fileupload"
                onClick={selectFile}
                >
                <i className="fas fa-file-upload" />
                <br />
                <br />
                Upload your photo here
                </button>
                <input type="file" id="getFile" style={{ display: "none" }} />
            </div>
                <div className="namedesc">
                    <h2>Write a name for your recipe</h2>
                    <label htmlFor="recipename">
                    Name: <input className="form-control" id="recipename" type="text" />
                    </label>
                    <h2>Write a short description</h2>
                    <textarea
                    id="descr"
                    className="form-control" 
                    placeholder="This is a cheap service..."
                    defaultValue={""}
                    />
                    <h2>Choose category/ies</h2>
                    <div className="checkgrid">
                    <label htmlFor="tech">
                        <input type="checkbox" />
                        Technology
                    </label>
                    <label htmlFor="tech">
                        <input type="checkbox" />
                        Technology
                    </label>
                    <label htmlFor="tech">
                        <input type="checkbox" />
                        Technology
                    </label>
                    </div>
                    <h2>Write your process</h2>
                    <textarea
                    id="cont"
                    className="form-control" 
                    placeholder="This is a cheap service..."
                    defaultValue={""}
                    />
                    <button className="btn btn-warning" onClick={addRec}>Submit</button>
                </div>
            </form>
        </section>
    )
}
