import './App.css';
import {initializeApp} from 'firebase/app';
import {getAuth, GoogleAuthProvider, signInWithPopup, createUserWithEmailAndPassword, signInWithEmailAndPassword} from 'firebase/auth';
import {useAuthState} from 'react-firebase-hooks/auth';
import Home from './Components/Home';
import LoginPage from './LoginPage';
import {useEffect, useState} from 'react';

const firebaseConfig = {
  apiKey: "AIzaSyD99Ps3ewiDL7WTtaCJaDqBmo5dmSRjuCo",
  authDomain: "cook-mastershare.firebaseapp.com",
  projectId: "cook-mastershare",
  storageBucket: "cook-mastershare.appspot.com",
  messagingSenderId: "901848339378",
  appId: "1:901848339378:web:ea76b02d8ef1cff6670aab",
  measurementId: "G-11NKHJEW95"
};

let app = initializeApp(firebaseConfig);
let auth = getAuth(app);

function App() {
  const [showLogin, setShowLogin] = useState(false);
  const [user] = useAuthState(auth);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  let emailVar = '';
  let passwordVar = '';
  async function signInWithGoogle(){
    let provider = new GoogleAuthProvider();
    await signInWithPopup(auth, provider);
  }
  async function signInWithEmail(){
    signInWithEmailAndPassword(auth, email, password);
  }
  function signOut(){
    auth.signOut();
  }
  if(showLogin){
    return <LoginPage signInWithGoogle={signInWithGoogle} email={emailVar} password={passwordVar} signInWithEmail={signInWithEmail}/>
  }
  return (
    <>
      {user ? <Home type={user.displayName} btnEvent={() => {setShowLogin(true)}}/> : <Home type="Login" btnEvent={() => setShowLogin(true)}/>}
    </>
  )
}

export default App;
